import styled from 'styled-components';
import Text from "../Text";

const RED = 'red';

const HeaderContainer = styled.header`
    background-color: tan;
    padding: 2rem;
    font-size:18px;
    & .wrapper {
        background-color: yellow;
    }
    &:hover {
        background-color: ${RED};
    }
`;


function Header() {
    return (
    <HeaderContainer>
        <Text text='Header'/> 
        <div className="wrapper">
        
     
        <h1>Header</h1>
        <h2>Sub Header</h2>
        
       <div>
           <h3>Card</h3>
           <p>Text</p>
       </div>
        </div> 
    
    </HeaderContainer>
    );
     
  
  }
  
  export default Header;
