import React from "react";


class Input extends React.Component {
    
    handleChange = (e) =>
        this.setState(prevState => ({
            values: {
                ...prevState.values,
                name: e.target.name,
            }
        }));
 

    render () {
        const {name, value, onChange } = this.state;
        return (
            <div>
                <input name={name} value={value} onChange={onChange}/>
            </div>
        );
    }
}
    
  export default Input;
