    state = {
        data: '',
    }
    
    updateData = (value) =>
        this.setState({
            data: value,
        });
 

    render () {
        const {data} = this.state;
        return (
            <div>
                <p>{data}</p>
                <Popup text='POPUP'/>
            </div>
        );
    }
}
    
  export default Footer;
